import { Fragment } from 'react';
import { Link } from 'react-router-dom';
import useInput from '../Hooks/SignUpHook';
import styles from './Signup.module.css';

// const isEmpty = (value) => value.trim() === ""; 

const Signup = () => {

    const b ={hello: "one"};

   const { value: first_name, valueChangeHandler: firstNameHandler, isValid: fNameIsValid } = useInput((value)=> value.trim() !== "");
   const { value: last_name, valueChangeHandler: lastNameHandler, isValid: lNameIsValid } = useInput((value)=> value.trim() !== "");
   const { value: email, valueChangeHandler: emailHandler, isValid: emailIsValid } = useInput((value)=> value.trim() !== "");
   const { value: contact, valueChangeHandler: contactHandler, isValid: contactIsValid } = useInput((value)=> value.trim() !== "");
   const { value: org_name, valueChangeHandler: orgNameHandler, isValid: orgNameIsValid } = useInput((value)=> value.trim() !== "");




    const signUphandler = (event) => {
        event.preventDefault();
    }


    return (<Fragment>
        <div className={styles.signin}> <p> Already have an account? </p><Link to="/">SIGN IN</Link></div>
        <main className={styles.signupContainer}>
            <section className={styles.form}>
                <div className={styles["form-detail"]}>
                    <h1>Sign up. For Free</h1>
                    <p>to access CRM.</p>
                </div>
                <form onSubmit={signUphandler} autoComplete="false">
                    <div className={styles["form-control"]}>
                        <label htmlFor="f_name">First Name</label>
                        <input type="text" id="f_name" name="first_name" onChange={(event)=> firstNameHandler(event)}/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="l_name">Last Name</label>
                        <input type="text" id="l_name" name="last_name" />
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="email">Work Email</label>
                        <input type="text" id="email" name="email" />
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="contact">Contact</label>
                        <input type="text" id="contact" name="contact" />
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="orgname">Organizations Name</label>
                        <input type="text" id="orgname" name="org_name" />
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="password">Password</label>
                        <input type="password" id="password" name="password"  autoComplete="false"/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="confirm_password">Confirm Password</label>
                        <input type="password" id="confirm_password" name="confirm_password" autoComplete="false"/>
                    </div>
                    <button className={styles.submitBtn}>Sign Up</button>
                </form>
            </section>
        </main>
        </Fragment>
    );

}

export default Signup;
