import { useHistory } from 'react-router';
import useForm from '../../Hooks/Leads/useForm';
import styles from './AddLead.module.css';
import { getLeadsFields, postLeadRecord } from '../../library/leadsFns';
import LoadingPage from '../../Pages/loadingPage';
import Input from '../Input/Input';
import { useState } from 'react';
import useHttp from '../../Hooks/httpHook';

const AddLead = () => {
    const { inputHandler, address, information, isLoading, error} = useForm(getLeadsFields);
    const { sendRequest } = useHttp( postLeadRecord);
    const [description, setDescription] = useState("");
    const history = useHistory();

    let informationFields, addressFields;

    const onCancelHandler = (event) => {
        event.preventDefault();
        history.goBack(-1);
    }

    if(address) {
         addressFields = Object.values(address).map(obj => <Input key={obj.id} label={obj.label} type={obj.type} name={obj.name} value={obj.value} section={obj.section} handleChange={inputHandler}/>) 
    }

    if(information) {
        informationFields = Object.values(information).map(obj => <Input key={obj.id} label={obj.label} type={obj.type} name={obj.name} value={obj.value} section={obj.section} handleChange={inputHandler}/>) 
    }

    const saveHandler = (e) => {
        e.preventDefault();
        let leadObj = { description };

        Object.values(address).map(leadAddress => {
            leadObj = {
                ...leadObj,
                [leadAddress.name] : leadAddress.value
            }
        });

        Object.values(information).map(leadInfo =>{
            leadObj = {
                ...leadObj,
                [leadInfo.name]: leadInfo.value 
            }
        });
        sendRequest()

    }

    return (
            <form className={styles.form} autoComplete="none">
                {isLoading && <LoadingPage/>}
                <div className={styles.formActions}>
                    <h2>Create Lead</h2>
                    <div>
                        <button className={styles.alt} onClick={onCancelHandler}>Cancel</button>
                        <button className={styles.alt}>Save and New</button>
                        <button type="submit" className={styles.save} onClick={saveHandler}>Save</button>
                    </div> 
                </div>
                <div className={styles.formfields}>
                    <h3>Lead Information</h3>
                    <div className={styles.inp}>
                        {informationFields}
                    </div>
                    <h3>Address Information</h3>
                    <div className={styles.inp}>
                        {addressFields}
                    </div>
                    <h3>Description Information</h3>
                    <div  className={styles.inp}>
                        <textarea type="text" name="description" onChange={(e) => setDescription(e.target.value)}></textarea>
                    </div>
                </div>       
            </form>
    )

}

export default AddLead;
