import { useHistory } from 'react-router';
import useForm from '../../Hooks/Leads/useForm';
import styles from './AddLead.module.css';
import { getLeadsFields } from '../../library/leadsFns';
import LoadingPage from '../../Pages/loadingPage';
import Input from '../Input/Input';

const AddLead = () => {
   const { inputHandler, address, information, isLoading, error} = useForm(getLeadsFields);
    const history = useHistory();
    let informationFields, addressFields;
    const onCancelHandler = (event) => {
        event.preventDefault();
        history.goBack(-1);
    }
    if(address) {
         addressFields = Object.values(address).map(obj => <Input key={obj.id} label={obj.label} type={obj.type} name={obj.name} value={obj.value} handleChange={inputHandler}/>) 
    }
    if(information) {
        informationFields = Object.values(information).map(obj => <Input key={obj.id} label={obj.label} type={obj.type} name={obj.name} value={obj.value} handleChange={inputHandler}/>) 
    }

    return (
            <form className={styles.form}>
                {isLoading && <LoadingPage/>}
                <div className={styles.formActions}>
                    <h2>Create Lead</h2>
                    <div>
                        <button className={styles.alt} onClick={onCancelHandler}>Cancel</button>
                        <button className={styles.alt}>Save and New</button>
                        <button className={styles.save}>Save</button>
                    </div> 
                </div>
                <div className={styles.formfields}>
                <h3>Lead Information</h3>
                <div style={{marginLeft:"9rem"}}>
                    {informationFields}
                </div>
                <h3>Address Information</h3>
                </div>       
            </form>
    )

}

export default AddLead;
