import React from 'react';
import { useState } from 'react';
import LoadingPage from '../../Pages/loadingPage';
import Filter from '../Filter/Filter';
import Record from '../Record/Record';
import styles from './LeadsContainer.module.css';

const LeadsContainer = ({ leadCols, leads, isLoading }) => {

    const [showFilterCriteria, setFilterCriteria] = useState(false);
    const [filterValue, setFilterValue] = useState("");

    const checkBoxHandler = (value) => {
        setFilterCriteria(prevState => !prevState);
        setFilterValue(value.label.toLowerCase().split(" ").join("_"));
    }


    let columns = <React.Fragment>
        <div style={{ minWidth: "3rem", position: "fixed", backgroundColor: "#fff", borderRadius: "10px 0 0 0" }} className={styles.column}>
            <input type="checkbox" />
        </div>
        {leadCols.map((key, index) => <div key={index} className={styles.column}>{key.label}</div>)}
    </React.Fragment>;

    return (<section className={styles.leadsContainer}>
                {isLoading && <LoadingPage />}

                <section className={styles.leadsfilter}>
                    <p>Filter Leads by:</p>
                    <Filter leadsCols={leadCols}/>
                    {leadCols.map((value, index) => <div key={index} style={{ display: "flex", alignItems: "center", padding: "0 .5rem" }}>
                        <input type="checkbox" onClick={()=> checkBoxHandler(value)}/>
                        <p style={{ fontWeight: "lighter" }}>{value.label}</p> 
                        { showFilterCriteria && filterValue === value.label.toLowerCase().split(" ").join("_") && <p style={{ fontWeight: "lighter", fontSize:"13px" }}>By:</p> }
                    </div>)}
                </section>
                <section className={styles.leadsrecords}>
                    <div className={styles["lead-columns"]}>
                        {columns}
                    </div>
                    {leads.map(lead => <Record lead={lead} key={lead.id} />)}
                </section>
            </section>);
}

export default LeadsContainer
