import {  faBackward } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useCallback, useEffect } from 'react';
import { useState } from 'react';
import { useParams } from 'react-router';
import useHttp from '../../Hooks/httpHook';
import LoadingTopBar from '../../Pages/LoadingTopBar';
import { getSingleLeadRecord } from '../../Store/Leads/leads-actions';
import styles from './ViewLead.module.css';

const ViewLead = () => {

    const params = useParams();
    const {orgId, leadId} = params;
    const [lead, setLead] = useState([]);
    const {sendRequest : fetchLeadRecord , isLoading, error} = useHttp(getSingleLeadRecord, false);

    useEffect(() => {
        fetchLeadRecord({orgId, leadId}, (leadRecord) => {
            setLead(leadRecord.record)
        });

    }, []);
    const navigateBackHandler = () => {

    }
    let displayRecord;
    if(lead) {
        displayRecord =  Object.keys(lead).map((leadR, index) => {
                    return <div className={styles.field} key={index}> 
                                <div className={styles.label}>{ leadR }</div> 
                                <div className={styles.value}>{ lead[leadR] || "-" }</div> 
                            </div>
                })

    }
    const clickHandler = useCallback((event) => {
        let command = event.currentTarget.getAttribute("data-icon");
        let value = event.currentTarget.getAttribute("data-value");
        document.execCommand(command,false, value);
    }, [])

    const toggleHandler = (event) => {
        const element = document.getElementById(`${event.currentTarget.getAttribute("id")}_options`);
        if(element.classList.contains(styles.hide)) {

            element.classList.remove(styles.hide);
            setTimeout(()=> {
                element.classList.add(styles.show);
            },300);

        } else if (element.classList.contains(styles.show)) {

            element.classList.remove(styles.show);
            setTimeout(()=> {
                element.classList.add(styles.hide);
            },300);

        }
    }

    const mouseDownHandler = (e) => e.preventDefault();

return <section className={styles.singleRecord}>
            { isLoading && <LoadingTopBar/> }
            <section className={styles["singleRecord-actions"]}>
                <div className={styles.back}>
                    <FontAwesomeIcon icon={faBackward} onClick={navigateBackHandler}/>
                    <p>{ "" || lead.last_name}</p>
                </div>
                <div className={styles.action}>
                    <button className={styles.convert}>Convert</button>
                    <button className={styles.edit}>Edit</button>
                    <button className={styles.clone}>Clone</button>
                    <button className={styles.delete}>Delete</button>
                </div>
            </section>
            <section className={styles.values}>
                <section className={styles.rightPane}>
                       <div className={styles.full}>
                            <h1 className={styles.leadInfo}>Lead Information:</h1>
                            <div className={styles.full}>  {displayRecord}</div>     
                       </div>
                       <div className={styles.notes}>
                            <h1>Notes:</h1>
                            <div className={styles.richTextOptions}>
                                <i className="fas fa-bold" data-icon="bold" data-value="" onClick={clickHandler} onMouseDown={mouseDownHandler}></i>
                                <i className="fas fa-italic" data-icon="italic" data-value="" onClick={clickHandler} onMouseDown={mouseDownHandler}></i>
                                <div id="alignment" className={styles.alignment} onClick={toggleHandler} onMouseDown={mouseDownHandler}>
                                    <i className="fas fa-align-justify"></i>
                                    <div id="alignment_options" className={ `${styles.alignmentOptions} ${styles.hide}` }>
                                        <i className="fas fa-align-left" data-value="" data-icon="justifyLeft" onClick={clickHandler} onMouseDown={mouseDownHandler}></i>
                                        <i className="fas fa-align-justify" data-value="" data-icon="justifyFull" onClick={clickHandler} onMouseDown={mouseDownHandler}></i>
                                        <i className="fas fa-align-right" data-value="" data-icon="justifyRight" onClick={clickHandler} onMouseDown={mouseDownHandler}></i>
                                    </div>
                                </div>
                                <div id="list-style" className={`${styles["list-style"]}`} onClick={toggleHandler} onMouseDown={mouseDownHandler}>
                                    <i class="fas fa-list-ul"></i>
                                    <div id="list-style_options" className={`${styles["list-style_options"]} ${styles.hide}`}>
                                        <div data-value="" data-icon="insertunorderedlist" onClick={clickHandler} onMouseDown={mouseDownHandler}><i class="fas fa-list-ul"></i><span className={styles.listStyleValue}>Unordered List</span></div>
                                        <div data-value="" data-icon="insertorderedlist" onClick={clickHandler} onMouseDown={mouseDownHandler}> <i class="fas fa-list-ol"></i><span className={styles.listStyleValue}>Ordered List</span></div>
                                    </div>
                                </div>
                                <div id="color" className={styles.color}>
                                    <div className={styles.color_options}>
                                        <ul class="ze_cb"><li style="background-color: rgb(255, 255, 255);"></li><li style="background-color: rgb(204, 238, 255);"></li><li style="background-color: rgb(204, 204, 255);"></li><li style="background-color: rgb(229, 204, 255);"></li><li style="background-color: rgb(255, 204, 255);"></li><li style="background-color: rgb(255, 204, 238);"></li><li style="background-color: rgb(255, 204, 204);"></li><li style="background-color: rgb(255, 229, 204);"></li><li style="background-color: rgb(255, 255, 204);"></li><li style="background-color: rgb(204, 255, 204);"></li><li style="background-color: rgb(204, 255, 255);"></li><li style="background-color: rgb(204, 204, 204);"></li><li style="background-color: rgb(153, 204, 255);"></li><li style="background-color: rgb(153, 153, 255);"></li><li style="background-color: rgb(204, 153, 255);"></li><li style="background-color: rgb(255, 153, 255);"></li><li style="background-color: rgb(255, 153, 204);"></li><li style="background-color: rgb(255, 153, 153);"></li><li style="background-color: rgb(255, 204, 153);"></li><li style="background-color: rgb(255, 255, 153);"></li><li style="background-color: rgb(153, 255, 153);"></li><li style="background-color: rgb(153, 255, 255);"></li><li style="background-color: rgb(192, 192, 192);"></li><li style="background-color: rgb(102, 204, 255);"></li><li class="sel" style="background-color: rgb(102, 102, 255);"></li><li style="background-color: rgb(178, 102, 255);"></li><li style="background-color: rgb(255, 102, 254);"></li><li style="background-color: rgb(255, 102, 178);"></li><li style="background-color: rgb(255, 102, 102);"></li><li style="background-color: rgb(255, 178, 102);"></li><li style="background-color: rgb(254, 255, 102);"></li><li style="background-color: rgb(102, 255, 102);"></li><li style="background-color: rgb(102, 254, 255);"></li><li style="background-color: rgb(153, 153, 153);"></li><li style="background-color: rgb(57, 156, 253);"></li><li style="background-color: rgb(51, 51, 255);"></li><li style="background-color: rgb(153, 51, 255);"></li><li style="background-color: rgb(255, 51, 254);"></li><li style="background-color: rgb(255, 51, 152);"></li><li style="background-color: rgb(255, 51, 51);"></li><li style="background-color: rgb(255, 153, 51);"></li><li style="background-color: rgb(254, 255, 51);"></li><li style="background-color: rgb(51, 255, 51);"></li><li style="background-color: rgb(51, 254, 255);"></li><li style="background-color: rgb(102, 102, 102);"></li><li style="background-color: rgb(51, 153, 255);"></li><li style="background-color: rgb(0, 0, 255);"></li><li style="background-color: rgb(119, 0, 255);"></li><li style="background-color: rgb(255, 0, 238);"></li><li style="background-color: rgb(255, 0, 119);"></li><li style="background-color: rgb(255, 0, 0);"></li><li style="background-color: rgb(255, 127, 0);"></li><li style="background-color: rgb(254, 255, 0);"></li><li style="background-color: rgb(0, 255, 0);"></li><li style="background-color: rgb(0, 254, 255);"></li><li style="background-color: rgb(68, 68, 68);"></li><li style="background-color: rgb(0, 101, 204);"></li><li style="background-color: rgb(0, 0, 204);"></li><li style="background-color: rgb(102, 0, 204);"></li><li style="background-color: rgb(204, 0, 203);"></li><li style="background-color: rgb(204, 0, 204);"></li><li style="background-color: rgb(204, 0, 0);"></li><li style="background-color: rgb(204, 102, 0);"></li><li style="background-color: rgb(203, 204, 0);"></li><li style="background-color: rgb(0, 204, 0);"></li><li style="background-color: rgb(0, 203, 204);"></li><li style="background-color: rgb(51, 51, 51);"></li><li style="background-color: rgb(0, 76, 153);"></li><li style="background-color: rgb(0, 0, 153);"></li><li style="background-color: rgb(75, 0, 153);"></li><li style="background-color: rgb(153, 0, 152);"></li><li style="background-color: rgb(153, 0, 76);"></li><li style="background-color: rgb(153, 0, 0);"></li><li style="background-color: rgb(153, 76, 0);"></li><li style="background-color: rgb(152, 153, 0);"></li><li style="background-color: rgb(0, 153, 0);"></li><li style="background-color: rgb(0, 152, 153);"></li><li style="background-color: rgb(0, 0, 0);"></li><li style="background-color: rgb(0, 51, 102);"></li><li style="background-color: rgb(0, 0, 102);"></li><li style="background-color: rgb(51, 0, 102);"></li><li style="background-color: rgb(102, 0, 102);"></li><li style="background-color: rgb(102, 0, 50);"></li><li style="background-color: rgb(102, 0, 0);"></li><li style="background-color: rgb(102, 51, 0);"></li><li style="background-color: rgb(102, 102, 0);"></li><li style="background-color: rgb(0, 102, 0);"></li><li style="background-color: rgb(0, 102, 102);"></li></ul>
                                    </div>
                                </div>
                            </div>
                            <div className={styles.noteDiv}>
                                <div className={styles.note} placeholder="Enter a note.." contentEditable={true} placeholder="Add a note..."></div>
                                <button className={styles.submitBtn} onMouseDown={mouseDownHandler}>Add Note.</button>
                            </div>

                       </div>
                </section>
            </section>
</section>
}

export default ViewLead;
