import { createSlice } from '@reduxjs/toolkit';

let initialState = { leads: [], canCreate: null, canEdit: null};

const leadsSlice = createSlice({
    name:"leads",
    initialState: initialState, 
    reducers: {
       replaceLeads: (state, action) =>{
            state.leads = action.payload.leads;
            state.canCreate = action.payload.canCreate;
            state.canEdit = action.payload.canEdit;
       }
    }
});

export const leadsActions = leadsSlice.actions;

export default leadsSlice;
