import { useState } from 'react';
import styles from './Filter.module.css';

const Filter = ({leadsCols}) => {
    const[showFilter, setShowFilter] = useState(false);
    const [filterValue, setFilterValue]= useState("");

    const createFilterCriteria = (key) => {
        setShowFilter(prevState => !prevState);
        setFilterValue(key.toLowerCase().split(" ").join("_"))

    }
    console.log(filterValue);
    return (
            <section className={styles.filterSection}>
                <p>Filter leads By:</p>
                { leadsCols.map((col, index)=> <div key={index} className={styles.filterOptions}>
                    <input type="checkbox" onClick={()=>{createFilterCriteria(col.label)}}/>
                    <p style={{fontWeight:"lighter"}}>{col.label}</p>
                    </div>)}
            </section>
    );

}

export default Filter;
