import { useContext } from "react";
import AuthContext from "../Store/AuthContext";

const url = "http://localhost:8080";



export const getLeadsFields = async () => {
    const ctx = useContext(AuthContext);

    const response = await fetch(`${url}/leads/leadstable`,{
        method:"GET",
        headers : {
            "Content-Type":"application/json"
        }
    });
    const responseData = await response.json();
    if(!response.ok) {
        let error = new Error(responseData.message || "Error Occured!")
        error.isValid = responseData.isValid || false;
        error.value = responseData.value || "";
        throw(error);
    }

    return responseData;
}

export const postLeadRecord = async(data) =>{
    const response = await fetch(`${url}/leads/addrecord`,{
        method:"POST",
        body: JSON.stringify(data),
        headers :{
            "Content-Type": "application/json"
        }
    });
    const responseData = await response.json();

    if(!response.ok) {
        let error = new Error(responseData.message);
        error.isValid = responseData.isValid || false;
        error.value = responseData.value || ""
        throw(error)
    }
    return responseData;
}
