import { useRef } from 'react';
import styles from './Signup.module.css';

const Signup = () => {

    const firstNameRef = useRef();
    const lastNameRef = useRef();
    const emailRef = useRef();
    const contactRef = useRef();
    const orgNameRef = useRef();
    const passwordRef = useRef();
    const confirmPasswordRef = useRef();

    const signUphandler = (event) => {
        event.preventDefault();
    }

    return (
        <main className={styles.signupContainer}>
            <section className={styles.form}>
                <div className={styles["form-detail"]}>
                    <h1>Sign up. For Free</h1>
                    <p>to access CRM.</p>
                </div>
                <form onSubmit={signUphandler}>
                    <div className={styles["form-control"]}>
                        <label htmlFor="f_name">First Name</label>
                        <input type="text" id="f_name" name="first_name" ref={firstNameRef}/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="l_name">Last Name</label>
                        <input type="text" id="l_name" name="last_name" ref={lastNameRef}/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="email">Email</label>
                        <input type="text" id="email" name="email"/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="contact">Contact</label>
                        <input type="text" id="contact" name="contact"/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="orgname">Organizations Name</label>
                        <input type="text" id="orgname" name="org_name"/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="password">Password</label>
                        <input type="password" id="password" name="password"/>
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="confirm_password">Confirm Password</label>
                        <input type="password" id="confirm_password" name="confirm_password"/>
                    </div>
                    <button className={styles.submitBtn}>Sign In</button>
                </form>
            </section>
        </main>
    );

}

export default Signup;
