import { useDispatch } from "react-redux";


export const filterConfigObj = [{
    filterName: "Is",
    filterMethod: is
}, {
    filterName: "Contains",
    filterMethod: contains
}]

const is = ({name, value}, records, actionReducer, reducerFn) => {
    const filterRecs = records.filter(record => record[name] === value);
    dispatch(actionReducer({reducerFn: filterRecs}))
}

const contains = () => {

