import { useReducer } from "react";

const initialState = {
    first_name:{
        value:"",
        isValid: false
    },
    last_name: {
        value:"",
        isValid: false
    },
    email:{
        value:"",
        isValid: false
    },
    contact: {
        value:"",
        isValid: false
    },
    org_name: {
        value:"",
        isValid: false
    },
    password:{
        value:"",
        isValid: false
    }
}

const inputStateReducer = (state, action) => {
    if(action.type ==="INPUT") {
        return { ...state, ...action.payload}
    }

}


const useInput = () => {
    const [inputState, dispatcher] = useReducer(inputStateReducer, initialState);

    const valueChangeHandler = (event) => {
        const {name, value} = event.target;

       const payload = {
        name: value
       }

        dispatcher({type:"INPUT", payload})
    }


    return {
        inputState,
        valueChangeHandler
    }

}

export default useInput;
