import { useState } from "react"


const useHttp = () => {

    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState({isValid: false, value:"", message:""});

    const sendRequest = async (requestConfig,sendData) => {
        setIsLoading(true);
        try {
            const response = await fetch(requestConfig.url, {
                method: requestConfig.method ? requestConfig.method: "GET",
                body: requestConfig.body ? JSON.stringify(requestConfig.body) : null,
                headers: {
                    "Content-Type" : "application/json"
                }
            });
            const responseData = await response.json();
            if(!response.ok) {
                setError({
                    isValid: responseData.isValid ? responseData.isValid : "",
                    value: responseData.value ? responseData.value : "",
                })
                throw new Error(responseData.message)
            }
            sendData(responseData);            
        } catch (error) {
            setIsLoading(false);
            setError(prevState => {
                return { ...prevState, message: error.message }
            })
        }
    }

    return {
        isLoading,
        sendRequest,
        error
    }

}

export default useHttp;
