import { Fragment } from 'react';
import { Link } from 'react-router-dom';
import useInput from '../Hooks/SignUpHook';
import styles from './Signup.module.css';

const isEmpty = (value) => value.trim() !== "" && value.trim().length < 3; 

const Signup = () => {

    let enteredPassword;

   const { value: first_name, valueChangeHandler: firstNameHandler, blurHandler: fNameBlurHandler ,isValid: fNameValid } = useInput(isEmpty);
   const { value: last_name, valueChangeHandler: lastNameHandler, blurHandler: lNameBlurHandler, isValid: lNameValid } = useInput(isEmpty);
   const { value: email, valueChangeHandler: emailHandler, blurHandler: emailBlurHandler, isValid: emailValid } = useInput(isEmpty);
   const { value: contact, valueChangeHandler: contactHandler, blurHandler: contactBlurHandler, isValid: contactValid } = useInput(isEmpty);
   const { value: org_name, valueChangeHandler: orgNameHandler, blurHandler: orgBlurHandler, isValid: orgNameValid } = useInput(isEmpty);
   const { value: password, valueChangeHandler: passwordHandler, blurHandler: passwordBlurHandler, isValid: passwordValid } = useInput(isEmpty);
   enteredPassword = password
   const { value: c_password, valueChangeHandler: cPasswordHandler, isValid: cPasswordValid } =
    useInput((value, password)=> { console.log(enteredPassword); return value.trim() !== "" && password === value});

    const signUphandler = (event) => {
        event.preventDefault();

        const signUpObj = {
            first_name,
            last_name,
            email,
            contact,
            org_name,
            password
        }
       //network request;
    }


    return (<Fragment>
        <div className={styles.signin}> <p> Already have an account? </p><Link to="/">SIGN IN</Link></div>
        <main className={styles.signupContainer}>
            <section className={styles.form}>
                <div className={styles["form-detail"]}>
                    <h1>Sign up. For Free</h1>
                    <p>to access CRM.</p>
                </div>
                <form onSubmit={signUphandler} autoComplete="false">
                    <div className={styles["form-control"]}>
                        <label htmlFor="f_name">First Name</label>
                        <input type="text" id="f_name" name="first_name" onChange={(event)=> firstNameHandler(event)} onBlur={fNameBlurHandler}/>
                        { fNameValid && <p className={styles.error}>Please enter a valid First Name.</p> }
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="l_name">Last Name</label>
                        <input type="text" id="l_name" name="last_name" onBlur={lNameBlurHandler} onChange={lastNameHandler}/>
                        { lNameValid && <p className={styles.error}>Please enter a valid Last Name.</p> }
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="email">Work Email</label>
                        <input type="text" id="email" name="email" onBlur={emailBlurHandler} onChange={emailBlurHandler}  onChange={emailHandler}/>
                        { emailValid && <p className={styles.error}>Please enter a valid Email.</p> }
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="contact">Contact</label>
                        <input type="text" id="contact" name="contact"  onBlur={contactBlurHandler} onChange={contactHandler}/>
                        { contactValid && <p className={styles.error}>Please enter a valid Contact number.</p> }
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="orgname">Organizations Name</label>
                        <input type="text" id="orgname" name="org_name"  onBlur= {orgBlurHandler} onChange={orgNameHandler}/>
                        { orgNameValid && <p className={styles.error}>Please enter a valid organizations name.</p> }
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="password">Password</label>
                        <input type="password" id="password" name="password"  autoComplete="false" onBlur={passwordBlurHandler} onChange={passwordHandler}/>
                        { passwordValid && <p className={styles.error}>Please enter a valid password. Should be of length 3.</p> }
                    </div>
                    <div className={styles["form-control"]}>
                        <label htmlFor="confirm_password">Confirm Password</label>
                        <input type="password" id="confirm_password" name="confirm_password" autoComplete="false" onChange={cPasswordHandler}/>
                        { cPasswordValid && <p className={styles.error}>Password doesn't match.</p> }
                    </div>
                    <button className={styles.submitBtn}>Sign Up</button>
                </form>
            </section>
        </main>
        </Fragment>
    );

}

export default Signup;
