import React, { useState } from 'react';
import styles from './Filter.module.css';

const Filter = ({leadsCols}) => {
    const[showFilter, setShowFilter] = useState(false);
    const [filterValue, setFilterValue]= useState("");
    const [showFilterMethod, setShowFilterMethod] = useState(false);
    const [filterByMethod, setFilterByMethod] = useState("");

    const filterMethods = ["is","contains"];

    const createFilterCriteria = (key) => {
        setShowFilter(prevState => !prevState);
        setFilterValue(key.toLowerCase().split(" ").join("_"))
    }
    console.log(filterValue, filterByMethod);
    return (
            <section className={styles.filterSection}>
                <p>Filter leads By:</p>
                { leadsCols.map((col, index)=> <React.Fragment> <div className={styles.filter}> <div key={index} className={styles.filterOptions}>
                    <input type="checkbox" onClick={()=>{createFilterCriteria(col.label)}}/>
                    <p className={styles.lighter}>{col.label}</p>
                    </div>
                   { showFilter && filterValue === col.label.toLowerCase().split(" ").join("_") && <div className={styles.filterCriteria}>
                       a { showFilterMethod && <ul className={styles.filterMethods}>{filterMethods.map((method, index)=><li onClick={()=>{setFilterByMethod(method)}} key={index}>{method}</li>)}</ul> }
                       </div>}
                 {  showFilter && filterValue === col.label.toLowerCase().split(" ").join("_") && <div className={styles.filterInput}> <input style={{minWidth:"4rem"}} name={filterValue} type="text"/> <button>Filter</button> </div>}
                   </div>
                    </React.Fragment>
                    )}
            </section>
    );

}

export default Filter;
