import { useEffect, useState, useCallback, useContext } from "react";
import useHttp from "../httpHook"
import AuthContext from "../../Store/Auth/AuthContext";
import { getFields } from "../../Store/Leads/leads-actions";

const useForm = () => {

    const { isLoading, error, sendRequest } =  useHttp(getFields);
    const [formObj, setFormObj]= useState({});  
    const ctx = useContext(AuthContext);

    useEffect(() => {
        sendRequest(ctx.orgId,ctx.token, (data)=> {
            setFormObj(data);
        })
    },[]);

  const inputHandler = useCallback((event, section) => {

     const { name, value } = event.target;
     const newFormObj = {...formObj};
     newFormObj[section][name].value = value;
     setFormObj({...newFormObj});

  },[formObj])
    return {
        inputHandler,
        isLoading,
        httpError: error,
        address: formObj.address,
        information: formObj.information
    }
}

export default useForm;
