import { useContext } from "react";
import { Fragment, useEffect } from "react"
import { useDispatch, useSelector } from "react-redux";
import LeadActions from '../Components/Leads/LeadsActions';
import LeadsContainer from "../Components/Leads/LeadsContainer";
import useHttp from "../Hooks/httpHook";
import AuthContext from "../Store/Auth/AuthContext";
import { getAllLeads, getLeadColumns } from "../Store/Leads/leads-actions";

const LeadsModule = () => {
    const ctx = useContext(AuthContext);
    const { sendRequest: getLeads, isLoading, error } = useHttp(getAllLeads, true);
    const { sendRequest: getColumns, isLoading: leadColumnsLoading, error: errorGettingLeads} = useHttp(getLeadColumns, true);

    useEffect(() => {
        getColumns(ctx.orgId);
        getLeads(ctx.orgId);
    },[]);

    const leadColumns = useSelector(state => state.leads.columns);
    const leads = useSelector(state => state.leads.leads);

    return <Fragment> 
                <LeadActions/>
                <LeadsContainer leadkeys={leadColumns || []} leads={leads}/>
            </Fragment>
}

export default LeadsModule;
